            xs.push(i)
        }

        function animate () {
            let xs = [];

            for (var i=0; i <= 500; i++) {
                xs.push(i)
            }

            let points = xs.map(x => {

                return [x, x]
            })

            let newPath = "M" + points.map(p => {
                return p[0] + "," + p[1]
            }).join(" L")

            console.log(newPath);

            this.state({
                path: "M10,50 L100,50 L90,30"
            });
            console.log("it works")
            requestAnimationFrame(animate)


        }
        animate()
    }



    render() {
this.bigAnimate()
        return(
            <div>
                <svg>
                    <path d={this.state.path}></path>
                </svg>
            </div>
        )
    }
}

export default WavySvg;
