import "./style.css";

class WavySvg extends Component {

    state = {

    }

    values = () => {
        let xs = [];

        for (var i=0; i <= 500; i++) {
            xs.push(i)
        }
        console.log(xs)
        return xs;
    }

    animate = () => {
        let xs = [];

        for (var i=0; i <= 500; i++) {
            xs.push(i)
        }

        let points = this.xs.map(x => {

            return [x, x]
        })

        let path = "M" + points.map(p => {
            return p[0] + "," + p[1]
        }).join(" L")

        requestAnimationFrame(this.animate)
        return path;
    }

    render() {
        return(
            <div>
                <svg>
                    <path d={this.animate().path}></path>
                </svg>
            </div>
        )
    }
}

export default WavySvg;
