import React , { Component } from "react";
import "./style.css";

class WavySvg extends Component {

    componentDidMount() {
        this.bigAnimate()
    }

    constructor(props) {

        super(props)

        this.state = {
            loopActive: false,
            shuffleActive: false,
          };

        this.onToggleLoop = this.onToggleLoop.bind(this);


        this.state = {
            path: "M10,10 L50,100 L90,50"
        };
      }

    bigAnimate = () => {
       let xs = [];
        for (var i=0; i <= 500; i++) {
            xs.push(i)
        }

        function animate () {
            let xs = [];

            for (var i=0; i <= 500; i++) {
                xs.push(i)
            }

            let points = xs.map(x => {

                return [x, x]
            })

            let newPath = "M" + points.map(p => {
                return p[0] + "," + p[1]
            }).join(" L")

            console.log("it works")
            requestAnimationFrame(animate)
            return newPath;


        }

        animate()
    }



    render() {
        return(
            <div>
                <svg>
                    <path d={this.state.path}></path>
                </svg>
            </div>
        )
    }
}

export default WavySvg;
