import "./style.css";

class WavySvg extends Component {

    constructor(props) {

        super(props)
        this.state = {path: "M10,10 L50,100 L90,50"};
      }

    bigAnimate = () => {
       let xs = [];
        for (var i=0; i <= 500; i++) {
            xs.push(i)
        }

        function animate () {
            let xs = [];

            for (var i=0; i <= 500; i++) {
                xs.push(i)
            }

            let points = xs.map(x => {

                return [x, x]
            })

            let newPath = "M" + points.map(p => {
                return p[0] + "," + p[1]
            }).join(" L")

            // this.state.path = newPath
            console.log("it works")
            console.log(this.state.path)
            requestAnimationFrame(animate)
            return newPath;

        }

        animate()
        console.log(this.newPath)

    }



    render() {
this.bigAnimate()
        return(
            <div>
                <svg>
                    <path d={this.state.path}></path>
                </svg>
            </div>
        )
    }
}

export default WavySvg;
